		@SPONSORSHIPOPPORTUNITYGROUPID uniqueidentifier = null,
		@SPONSORSHIPLOCATIONID uniqueidentifier = null,
		@FIRSTNAME nvarchar(50) = '',
		@MIDDLENAME nvarchar(50) = '',
		@LASTNAME nvarchar(100)='',
