		@TWOMOSTFAVORITETHINGS nvarchar(1000) = '',
		@OTHERTHINGSTHECHILDENJOYS nvarchar(1000) = '',
		@CHILDPERSONALITY nvarchar(1000) = '',
		@CHILDHEALTH nvarchar(1000) = '',
		@SPIRITUALDEVELOPMENT nvarchar(1000) = '',
		@ATTENDINGSCHOOL bit = 0,
		@REASONNOTATTENDINGSCHOOL nvarchar(255) = '',
		@CLASSLEVEL nvarchar(20) = '',
		@FAVORITESUBJECT nvarchar(1000) = '',
		@VOCATIONALSKILLS nvarchar(1000) = '',
		@ADDITIONALCHILDINFORMATION nvarchar(max) = '',
		@CCHENTEREDBY nvarchar(100) = '',
		@CCHTEMPID nvarchar(250) = '',
		@CURRENTPHOTODATE datetime = null,
		@PHOTOSCANNED bit = 0,
		@CHILDPROFILESTATUSCODEID uniqueidentifier = null,
		@CHILDPROFILEUPDATECODEID uniqueidentifier = null,
		@PROFILEUPDATENOTIFICATION bit = 0,
		@TEMPCHILDID nvarchar(50) = '',
		@FUNDED bit = 0
)
as

set nocount on;

if @ID is null
		set @ID = newid()

if @CHANGEAGENTID is null  
		exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output

declare @CURRENTDATE datetime
set @CURRENTDATE = getdate()

begin try
	-- handle inserting the data
	insert into dbo.USR_BATCHCHILDEXTENSION
		 (ID, BATCHID, SEQUENCE,
		 SPONSORSHIPOPPORTUNITYGROUPID, 
	SPONSORSHIPLOCATIONID, 
	FIRSTNAME, 
	MIDDLENAME, 
	LASTNAME, 
	GENDERCODE, 
	BIRTHDATE, 
	PICTURE, 
	ISHIVPOSITIVE, 
	ISORPHANED, 
	SPROPPCHILDCONDITIONCODEID,
	TEMPCHILDID,
	BIRTHDATEACCURACYCODEID, 
	FUNDED,
	PROGRAMCOMPLETIONDATE, CHILDNAMESUFFIXCODEID, CHILDLIVESWITHCODEID, FATHERWORKSASCODEID, FATHERWORKSASOTHER, MOTHERWORKSASCODEID, MOTHERWORKSASOTHER, CAREGIVERRELATIONCODEID, CAREGIVERRELATIONOTHER, CAREGIVERWORKSASCODEID, CAREGIVERWORKSASOTHER, CAREGIVERREASONCODEID, REASONFORCAREGIVEROTHER, NUMBEROFSISTERS, NUMBEROFBROTHERS, HOUSINGWALLWOOD, HOUSINGWALLMUD, HOUSINGWALLBAMBOO, HOUSINGWALLBLOCK, HOUSINGWALLOTHER, ROOFINGTIN, ROOFINGGRASSLEAVES, ROOFINGWOOD, ROOFINGTILE, ROOFINGOTHER, WATERSOURCEINDOOR, WATERSOURCECOMMUNITYTAP, WATERSOURCEBOREHOLEWELL, WATERSOURCERIVER, WATERSOURCEOTHER, COOKINGSOURCEWOODFIRE, COOKINGSOURCEELECTRICSTOVE, COOKINGSOURCEGASSTOVE, COOKINGSOURCEOTHER, LIGHTSOURCEELECTRICITY, LIGHTSOURCEOILLAMP, LIGHTSOURCEGENERATOR, LIGHTSOURCENONE, LIGHTSOURCEOTHER, AREADESCRIPTION, MYFAVORITETHINGTODO, WHENIPLAYWITHFRIENDSWE, WHENATHOMEIHELPOUTBY, WHENITALKTOGODIASKHIM, WHENIAMATSCHOOLILIKELEARNING, SOMEDAYI, TWOMOSTFAVORITETHINGS, OTHERTHINGSTHECHILDENJOYS, CHILDPERSONALITY, CHILDHEALTH, SPIRITUALDEVELOPMENT, ATTENDINGSCHOOL, REASONNOTATTENDINGSCHOOL, CLASSLEVEL, FAVORITESUBJECT, VOCATIONALSKILLS, ADDITIONALCHILDINFORMATION, CCHENTEREDBY, CCHTEMPID, CURRENTPHOTODATE, PHOTOSCANNED, CHILDPROFILESTATUSCODEID, CHILDPROFILEUPDATECODEID, PROFILEUPDATENOTIFICATION, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED)
		values
		(@ID, @BATCHID,  @SEQUENCE,
		@SPONSORSHIPOPPORTUNITYGROUPID, 
	@SPONSORSHIPLOCATIONID, 
	@FIRSTNAME, 
	@MIDDLENAME, 
	@LASTNAME, 
	@GENDERCODE, 
	@BIRTHDATE, 
	@PICTURE, 
	@ISHIVPOSITIVE, 
	@ISORPHANED, 
	@SPROPPCHILDCONDITIONCODEID,
	@TEMPCHILDID,
	@BIRTHDATEACCURACYCODEID, 
	@FUNDED,
	@PROGRAMCOMPLETIONDATE, @CHILDNAMESUFFIXCODEID, @CHILDLIVESWITHCODEID, @FATHERWORKSASCODEID, @FATHERWORKSASOTHER, @MOTHERWORKSASCODEID, @MOTHERWORKSASOTHER, @CAREGIVERRELATIONCODEID, @CAREGIVERRELATIONOTHER, @CAREGIVERWORKSASCODEID, @CAREGIVERWORKSASOTHER, @CAREGIVERREASONCODEID, @REASONFORCAREGIVEROTHER, @NUMBEROFSISTERS, @NUMBEROFBROTHERS, @HOUSINGWALLWOOD, @HOUSINGWALLMUD, @HOUSINGWALLBAMBOO, @HOUSINGWALLBLOCK, @HOUSINGWALLOTHER, @ROOFINGTIN, @ROOFINGGRASSLEAVES, @ROOFINGWOOD, @ROOFINGTILE, @ROOFINGOTHER, @WATERSOURCEINDOOR, @WATERSOURCECOMMUNITYTAP, @WATERSOURCEBOREHOLEWELL, @WATERSOURCERIVER, @WATERSOURCEOTHER, @COOKINGSOURCEWOODFIRE, @COOKINGSOURCEELECTRICSTOVE, @COOKINGSOURCEGASSTOVE, @COOKINGSOURCEOTHER, @LIGHTSOURCEELECTRICITY, @LIGHTSOURCEOILLAMP, @LIGHTSOURCEGENERATOR, @LIGHTSOURCENONE, @LIGHTSOURCEOTHER, @AREADESCRIPTION, @MYFAVORITETHINGTODO, @WHENIPLAYWITHFRIENDSWE, @WHENATHOMEIHELPOUTBY, @WHENITALKTOGODIASKHIM, @WHENIAMATSCHOOLILIKELEARNING, @SOMEDAYI, @TWOMOSTFAVORITETHINGS, @OTHERTHINGSTHECHILDENJOYS, @CHILDPERSONALITY, @CHILDHEALTH, @SPIRITUALDEVELOPMENT, @ATTENDINGSCHOOL, @REASONNOTATTENDINGSCHOOL, @CLASSLEVEL, @FAVORITESUBJECT, @VOCATIONALSKILLS, @ADDITIONALCHILDINFORMATION, @CCHENTEREDBY, @CCHTEMPID, @CURRENTPHOTODATE, @PHOTOSCANNED, @CHILDPROFILESTATUSCODEID, @CHILDPROFILEUPDATECODEID, @PROFILEUPDATENOTIFICATION, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE)
	
end try

begin catch
		exec dbo.USP_RAISE_ERROR
		return 1
end catch

