				@BIRTHDATE = BIRTHDATE, 
				@ISHIVPOSITIVE = ISHIVPOSITIVE, 
				@SPROPPCHILDCONDITIONCODEID = SPROPPCHILDCONDITIONCODEID, 
				@ISORPHANED = ISORPHANED, 
				@PICTURE = PICTURE,
				@CCHZIPFILENAME = CCHZIPFILENAME
	from dbo.USR_BATCHCHILDEXTENSION
	where ID = @ID

	return 0;

