						CHILDLIVESWITHCODEID = @CHILDLIVESWITHCODEID,
						FATHERWORKSASCODEID = @FATHERWORKSASCODEID,
						FATHERWORKSASOTHER = @FATHERWORKSASOTHER,
						MOTHERWORKSASCODEID = @MOTHERWORKSASCODEID,
						MOTHERWORKSASOTHER = @MOTHERWORKSASOTHER,
