create procedure dbo.USP_DATAFORMTEMPLATE_ADD_CHILDEXTENSION_BATCH_TABLE
(
    @ID uniqueidentifier = null output,
    @CHANGEAGENTID uniqueidentifier = null,
    @BATCHID uniqueidentifier,
    @SEQUENCE int,
    @BIRTHDATEACCURACYCODEID uniqueidentifier = null,
    @PROGRAMCOMPLETIONDATE datetime = null,
    @CHILDNAMESUFFIXCODEID uniqueidentifier = null,
    @CHILDLIVESWITHCODEID uniqueidentifier = null,
    @FATHERWORKSASCODEID uniqueidentifier,
    @FATHERWORKSASOTHER nvarchar(50) = '',
    @MOTHERWORKSASCODEID uniqueidentifier,
    @MOTHERWORKSASOTHER nvarchar(50) = '',
    @CAREGIVERRELATIONCODEID uniqueidentifier = null,
    @CAREGIVERRELATIONOTHER nvarchar(50) = '',
    @CAREGIVERWORKSASCODEID uniqueidentifier = null,
    @CAREGIVERWORKSASOTHER nvarchar(50) = '',
    @CAREGIVERREASONCODEID uniqueidentifier = null,
    @REASONFORCAREGIVEROTHER nvarchar(50) = '',
    @NUMBEROFSISTERS tinyint = 0,
    @NUMBEROFBROTHERS tinyint = 0,
    @HOUSINGWALLWOOD bit = 0,
    @HOUSINGWALLMUD bit = 0,
    @HOUSINGWALLBAMBOO bit = 0,
    @HOUSINGWALLBLOCK bit = 0,
    @HOUSINGWALLOTHER nvarchar(50) = '',
    @ROOFINGTIN bit = 0,
    @ROOFINGGRASSLEAVES bit = 0,
    @ROOFINGWOOD bit = 0,
    @ROOFINGTILE bit = 0,
    @ROOFINGOTHER nvarchar(50) = '',
    @WATERSOURCEINDOOR bit = 0,
    @WATERSOURCECOMMUNITYTAP bit = 0,
    @WATERSOURCEBOREHOLEWELL bit = 0,
    @WATERSOURCERIVER bit = 0,
    @WATERSOURCEOTHER nvarchar(50) = '',
    @COOKINGSOURCEWOODFIRE bit = 0,
    @COOKINGSOURCEELECTRICSTOVE bit = 0,
    @COOKINGSOURCEGASSTOVE bit = 0,
    @COOKINGSOURCEOTHER nvarchar(50) = '',
    @LIGHTSOURCEELECTRICITY bit = 0,
    @LIGHTSOURCEOILLAMP bit = 0,
    @LIGHTSOURCEGENERATOR bit = 0,
    @LIGHTSOURCENONE bit = 0,
    @LIGHTSOURCEOTHER nvarchar(50) = '',
    @AREADESCRIPTION nvarchar(1000) = '',
    @MYFAVORITETHINGTODO nvarchar(1000) = '',
    @WHENIPLAYWITHFRIENDSWE nvarchar(1000) = '',
    @WHENATHOMEIHELPOUTBY nvarchar(1000) = '',
    @WHENITALKTOGODIASKHIM nvarchar(1000) = '',
    @WHENIAMATSCHOOLILIKELEARNING nvarchar(1000) = '',
    @SOMEDAYI nvarchar(1000) = '',
    @TWOMOSTFAVORITETHINGS nvarchar(1000) = '',
    @OTHERTHINGSTHECHILDENJOYS nvarchar(1000) = '',
    @CHILDPERSONALITY nvarchar(1000) = '',
    @CHILDHEALTH nvarchar(1000) = '',
    @SPIRITUALDEVELOPMENT nvarchar(1000) = '',
    @ATTENDINGSCHOOL bit = 0,
    @REASONNOTATTENDINGSCHOOL nvarchar(255) = '',
    @CLASSLEVEL nvarchar(20) = '',
    @FAVORITESUBJECT nvarchar(1000) = '',
    @VOCATIONALSKILLS nvarchar(1000) = '',
    @ADDITIONALCHILDINFORMATION nvarchar(max) = '',
    @CCHENTEREDBY nvarchar(100) = '',
    @CCHTEMPID nvarchar(250) = '',
    @CURRENTPHOTODATE datetime = null,
    @PHOTOSCANNED bit = 0,
    @CHILDPROFILESTATUSCODEID uniqueidentifier,
    @CHILDPROFILEUPDATECODEID uniqueidentifier = null,
    @PROFILEUPDATENOTIFICATION bit = 0,
    @FUNDED bit = 0,
    @TEMPCHILDID nvarchar(50),
    @FIRSTNAME nvarchar(50) = '',
    @MIDDLENAME nvarchar(50) = '',
    @LASTNAME nvarchar(100),
    @GENDERCODE tinyint = 0,
    @BIRTHDATE dbo.UDT_FUZZYDATE,
    @ISHIVPOSITIVE bit = 0,
    @SPROPPCHILDCONDITIONCODEID uniqueidentifier = null,
    @ISORPHANED bit = 0,
    @PICTURE varbinary(max)
)
as

set nocount on;

if @ID is null
    set @ID = newid()

if @CHANGEAGENTID is null  
    exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output

declare @CURRENTDATE datetime
set @CURRENTDATE = getdate()

begin try
	-- handle inserting the data
	insert into dbo.USR_BATCHCHILDEXTENSION
		(ID, BATCHID, SEQUENCE, BIRTHDATEACCURACYCODEID, PROGRAMCOMPLETIONDATE, CHILDNAMESUFFIXCODEID, CHILDLIVESWITHCODEID, FATHERWORKSASCODEID, FATHERWORKSASOTHER, MOTHERWORKSASCODEID, MOTHERWORKSASOTHER, CAREGIVERRELATIONCODEID, CAREGIVERRELATIONOTHER, CAREGIVERWORKSASCODEID, CAREGIVERWORKSASOTHER, CAREGIVERREASONCODEID, REASONFORCAREGIVEROTHER, NUMBEROFSISTERS, NUMBEROFBROTHERS, HOUSINGWALLWOOD, HOUSINGWALLMUD, HOUSINGWALLBAMBOO, HOUSINGWALLBLOCK, HOUSINGWALLOTHER, ROOFINGTIN, ROOFINGGRASSLEAVES, ROOFINGWOOD, ROOFINGTILE, ROOFINGOTHER, WATERSOURCEINDOOR, WATERSOURCECOMMUNITYTAP, WATERSOURCEBOREHOLEWELL, WATERSOURCERIVER, WATERSOURCEOTHER, COOKINGSOURCEWOODFIRE, COOKINGSOURCEELECTRICSTOVE, COOKINGSOURCEGASSTOVE, COOKINGSOURCEOTHER, LIGHTSOURCEELECTRICITY, LIGHTSOURCEOILLAMP, LIGHTSOURCEGENERATOR, LIGHTSOURCENONE, LIGHTSOURCEOTHER, AREADESCRIPTION, MYFAVORITETHINGTODO, WHENIPLAYWITHFRIENDSWE, WHENATHOMEIHELPOUTBY, WHENITALKTOGODIASKHIM, WHENIAMATSCHOOLILIKELEARNING, SOMEDAYI, TWOMOSTFAVORITETHINGS, OTHERTHINGSTHECHILDENJOYS, CHILDPERSONALITY, CHILDHEALTH, SPIRITUALDEVELOPMENT, ATTENDINGSCHOOL, REASONNOTATTENDINGSCHOOL, CLASSLEVEL, FAVORITESUBJECT, VOCATIONALSKILLS, ADDITIONALCHILDINFORMATION, CCHENTEREDBY, CCHTEMPID, CURRENTPHOTODATE, PHOTOSCANNED, CHILDPROFILESTATUSCODEID, CHILDPROFILEUPDATECODEID, PROFILEUPDATENOTIFICATION, FUNDED, TEMPCHILDID, FIRSTNAME, MIDDLENAME, LASTNAME, GENDERCODE, BIRTHDATE, ISHIVPOSITIVE, SPROPPCHILDCONDITIONCODEID, ISORPHANED, PICTURE, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED)
    values
		(@ID, @BATCHID, @SEQUENCE, @BIRTHDATEACCURACYCODEID, @PROGRAMCOMPLETIONDATE, @CHILDNAMESUFFIXCODEID, @CHILDLIVESWITHCODEID, @FATHERWORKSASCODEID, @FATHERWORKSASOTHER, @MOTHERWORKSASCODEID, @MOTHERWORKSASOTHER, @CAREGIVERRELATIONCODEID, @CAREGIVERRELATIONOTHER, @CAREGIVERWORKSASCODEID, @CAREGIVERWORKSASOTHER, @CAREGIVERREASONCODEID, @REASONFORCAREGIVEROTHER, @NUMBEROFSISTERS, @NUMBEROFBROTHERS, @HOUSINGWALLWOOD, @HOUSINGWALLMUD, @HOUSINGWALLBAMBOO, @HOUSINGWALLBLOCK, @HOUSINGWALLOTHER, @ROOFINGTIN, @ROOFINGGRASSLEAVES, @ROOFINGWOOD, @ROOFINGTILE, @ROOFINGOTHER, @WATERSOURCEINDOOR, @WATERSOURCECOMMUNITYTAP, @WATERSOURCEBOREHOLEWELL, @WATERSOURCERIVER, @WATERSOURCEOTHER, @COOKINGSOURCEWOODFIRE, @COOKINGSOURCEELECTRICSTOVE, @COOKINGSOURCEGASSTOVE, @COOKINGSOURCEOTHER, @LIGHTSOURCEELECTRICITY, @LIGHTSOURCEOILLAMP, @LIGHTSOURCEGENERATOR, @LIGHTSOURCENONE, @LIGHTSOURCEOTHER, @AREADESCRIPTION, @MYFAVORITETHINGTODO, @WHENIPLAYWITHFRIENDSWE, @WHENATHOMEIHELPOUTBY, @WHENITALKTOGODIASKHIM, @WHENIAMATSCHOOLILIKELEARNING, @SOMEDAYI, @TWOMOSTFAVORITETHINGS, @OTHERTHINGSTHECHILDENJOYS, @CHILDPERSONALITY, @CHILDHEALTH, @SPIRITUALDEVELOPMENT, @ATTENDINGSCHOOL, @REASONNOTATTENDINGSCHOOL, @CLASSLEVEL, @FAVORITESUBJECT, @VOCATIONALSKILLS, @ADDITIONALCHILDINFORMATION, @CCHENTEREDBY, @CCHTEMPID, @CURRENTPHOTODATE, @PHOTOSCANNED, @CHILDPROFILESTATUSCODEID, @CHILDPROFILEUPDATECODEID, @PROFILEUPDATENOTIFICATION, @FUNDED, @TEMPCHILDID, @FIRSTNAME, @MIDDLENAME, @LASTNAME, @GENDERCODE, @BIRTHDATE, @ISHIVPOSITIVE, @SPROPPCHILDCONDITIONCODEID, @ISORPHANED, @PICTURE, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE)
end try

begin catch
    exec dbo.USP_RAISE_ERROR
    return 1
end catch

return 0
