LoadImplementation.c:CreateProcedureSQL:
create procedure dbo.USP_DATAFORMTEMPLATE_EDITLOAD_TESTINGONLY
(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
    @TESTMEMO nvarchar(max) = null output,
    @TESTINGTEXT nvarchar(50) = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@TSLONG = TSLONG,
        @TESTMEMO = TESTMEMO, 
        @TESTINGTEXT = TESTINGTEXT
	from dbo.USR_TESTINGONLY
	where ID = @ID

	return 0;

SaveImplementation.c:CreateProcedureSQL:
create procedure dbo.USP_DATAFORMTEMPLATE_EDIT_TESTINGONLY ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @TESTMEMO nvarchar(max), @TESTINGTEXT nvarchar(50) ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
begin try -- handle updating the data update dbo.USR_TESTINGONLY set TESTMEMO = @TESTMEMO, TESTINGTEXT = @TESTINGTEXT,
CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = getdate() where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
