/* invoke the default implementation */
declare @r int
exec @r = dbo.USP_DATAFORMTEMPLATE_ADD_SPONSORSHIPOPPORTUNITY_CHILD 
  @ID = @ID output, 
  @CURRENTAPPUSERID = @CURRENTAPPUSERID, 
  @CHANGEAGENTID = @CHANGEAGENTID, 
  @SPONSORSHIPOPPORTUNITYGROUPID = @SPONSORSHIPOPPORTUNITYGROUPID, 
  @SPONSORSHIPLOCATIONID = @SPONSORSHIPLOCATIONID, 
  @FIRSTNAME = @FIRSTNAME, 
  @MIDDLENAME = @MIDDLENAME, 
  @LASTNAME = @LASTNAME, 
  @GENDERCODE = @GENDERCODE, 
  @BIRTHDATE = @BIRTHDATE, 
  @PICTURE = @PICTURE, 
  @PICTURETHUMBNAIL = @PICTURETHUMBNAIL, 
  @PICTURECHANGED = @PICTURECHANGED, 
  @ISHIVPOSITIVE = @ISHIVPOSITIVE, 
  @ISORPHANED = @ISORPHANED, 
  @SPROPPCHILDCONDITIONCODEID = @SPROPPCHILDCONDITIONCODEID

if @@error <> 0 
  begin
    if @r <> 0 return @r
    return 1;
  end


begin try
  -- Handle any custom "after-save" logic here 
  --get the SPONSORSHIPOPPORTUNITYCHILDID that was just created:
  declare @CURRENTDATE datetime                      -- used for the DATEADDED and DATECHANGED of the new USR_CHILDEXTENSION row
  declare @newChildExtensionID uniqueidentifier      -- used for the ID value of the new USR_CHILDEXTENSION row
  set @CURRENTDATE = getdate()
  --print @ID
  select @SPONSORSHIPOPPORTUNITYCHILDID = ID from dbo.SPONSORSHIPOPPORTUNITY where ID = @ID      -- get the newly created SPONSORSHIPOPPORTUNITY.ID value to store as the new SPONSORSHIPOPPORTUNITYCHILDID 
  --print @SPONSORSHIPOPPORTUNITYCHILDID
  --set @ID = newid()
  set @newChildExtensionID = newid()

  --insert into childextension table:
  insert into dbo.USR_CHILDEXTENSION
    (ID, SPONSORSHIPOPPORTUNITYCHILDID, SIBLINGSID, BIRTHDATEACCURACYCODEID, CURRENTPROGRAMCOMPLETIONDATE, CHILDNAMESUFFIXCODEID, CHILDLIVESWITHCODEID, FATHERWORKSASCODEID, FATHERWORKSASOTHER, MOTHERWORKSASCODEID, MOTHERWORKSASOTHER, CAREGIVERRELATIONCODEID, CAREGIVERRELATIONOTHER, CAREGIVERWORKSASCODEID, CAREGIVERWORKSASOTHER, CAREGIVERREASONCODEID, REASONFORCAREGIVEROTHER, NUMBEROFSISTERS, NUMBEROFBROTHERS, HOUSINGWALLWOOD, HOUSINGWALLMUD, HOUSINGWALLBAMBOO, HOUSINGWALLBLOCK, HOUSINGWALLOTHER, ROOFINGTIN, ROOFINGGRASSLEAVES, ROOFINGWOOD, ROOFINGTILE, ROOFINGOTHER, WATERSOURCEINDOOR, WATERSOURCECOMMUNITYTAP, WATERSOURCEBOREHOLEWELL, WATERSOURCERIVER, WATERSOURCEOTHER, COOKINGSOURCEWOODFIRE, COOKINGSOURCEELECTRICSTOVE, COOKINGSOURCEGASSTOVE, COOKINGSOURCEOTHER, LIGHTSOURCEELECTRICITY, LIGHTSOURCEOILLAMP, LIGHTSOURCEGENERATOR, LIGHTSOURCENONE, LIGHTSOURCEOTHER, AREADESCRIPTION, MYFAVORITETHINGTODO, WHENIPLAYWITHFRIENDSWE, WHENATHOMEIHELPOUTBY, WHENITALKTOGODIASKHIM, WHENIAMATSCHOOLILIKELEARNING, SOMEDAYI, TWOMOSTFAVORITETHINGS, OTHERTHINGSTHECHILDENJOYS, CHILDPERSONALITY, CHILDHEALTH, SPIRITUALDEVELOPMENT, ATTENDINGSCHOOL, REASONNOTATTENDINGSCHOOL, CLASSLEVEL, FAVORITESUBJECT, VOCATIONALSKILLS, ADDITIONALCHILDINFORMATION, CCHENTEREDBY, CCHTEMPID, CURRENTPHOTODATE, PHOTOSTORED, CHILDPROFILESTATUSCODEID, CHILDPROFILEUPDATECODEID, PROFILEUPDATENOTIFICATION, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED)
    values
    (@newChildExtensionID, @SPONSORSHIPOPPORTUNITYCHILDID, @SIBLINGSID, @BIRTHDATEACCURACYCODEID, @CURRENTPROGRAMCOMPLETIONDATE, @CHILDNAMESUFFIXCODEID, @CHILDLIVESWITHCODEID, @FATHERWORKSASCODEID, @FATHERWORKSASOTHER, @MOTHERWORKSASCODEID, @MOTHERWORKSASOTHER, @CAREGIVERRELATIONCODEID, @CAREGIVERRELATIONOTHER, @CAREGIVERWORKSASCODEID, @CAREGIVERWORKSASOTHER, @CAREGIVERREASONCODEID, @REASONFORCAREGIVEROTHER, @NUMBEROFSISTERS, @NUMBEROFBROTHERS, @HOUSINGWALLWOOD, @HOUSINGWALLMUD, @HOUSINGWALLBAMBOO, @HOUSINGWALLBLOCK, @HOUSINGWALLOTHER, @ROOFINGTIN, @ROOFINGGRASSLEAVES, @ROOFINGWOOD, @ROOFINGTILE, @ROOFINGOTHER, @WATERSOURCEINDOOR, @WATERSOURCECOMMUNITYTAP, @WATERSOURCEBOREHOLEWELL, @WATERSOURCERIVER, @WATERSOURCEOTHER, @COOKINGSOURCEWOODFIRE, @COOKINGSOURCEELECTRICSTOVE, @COOKINGSOURCEGASSTOVE, @COOKINGSOURCEOTHER, @LIGHTSOURCEELECTRICITY, @LIGHTSOURCEOILLAMP, @LIGHTSOURCEGENERATOR, @LIGHTSOURCENONE, @LIGHTSOURCEOTHER, @AREADESCRIPTION, @MYFAVORITETHINGTODO, @WHENIPLAYWITHFRIENDSWE, @WHENATHOMEIHELPOUTBY, @WHENITALKTOGODIASKHIM, @WHENIAMATSCHOOLILIKELEARNING, @SOMEDAYI, @TWOMOSTFAVORITETHINGS, @OTHERTHINGSTHECHILDENJOYS, @CHILDPERSONALITY, @CHILDHEALTH, @SPIRITUALDEVELOPMENT, @ATTENDINGSCHOOL, @REASONNOTATTENDINGSCHOOL, @CLASSLEVEL, @FAVORITESUBJECT, @VOCATIONALSKILLS, @ADDITIONALCHILDINFORMATION, @CCHENTEREDBY, @CCHTEMPID, @CURRENTPHOTODATE, @PHOTOSTORED, @CHILDPROFILESTATUSCODEID, @CHILDPROFILEUPDATECODEID, @PROFILEUPDATENOTIFICATION, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE)


end try
begin catch
  exec dbo.USP_RAISE_ERROR;
  return 1;
