        create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_CHILDPHOTOATTACHMENT(
					@ID uniqueidentifier,
					@DATALOADED bit = 0 output,
					@DATEENTERED datetime = null output, 
					@TITLE nvarchar(50) = null output,
					@AUTHORID uniqueidentifier = null output,
					@ATTACHMENTTYPECODEID uniqueidentifier = null output,
					@FILE varbinary(max) = null output,
					@FILENAME nvarchar(255) = null output,
					@DISPLAYEDFILENAME nvarchar(255) = null output,
					@FILECHANGED bit = null output,
					@TSLONG bigint = 0 output
				)
				as
					set nocount on;
          set @DATALOADED = 0;
					set @TSLONG = 0;

					select 
						@DATALOADED = 1,
						@DATEENTERED = [DATEENTERED],
						@TITLE = [TITLE],
						@AUTHORID = [AUTHORID],
						@ATTACHMENTTYPECODEID = [SPROPPATTACHMENTTYPECODEID],
						@FILE = null,    /* DO NOT LOAD THE FILE HERE, IT WILL BE DOWNLOADED ON DEMAND */
						@FILENAME = [FILENAME],
						@DISPLAYEDFILENAME = [FILENAME],
						@TSLONG = [TSLONG]
					from
						dbo.[SPONSORSHIPOPPORTUNITYATTACHMENT]
					where
						[ID] = @ID;

					return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_CHILDPHOTOTATTACHMENT ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @DATEENTERED datetime, @TITLE nvarchar(50), @AUTHORID uniqueidentifier, @ATTACHMENTTYPECODEID uniqueidentifier, @FILE varbinary(max), @FILENAME nvarchar(255), @DISPLAYEDFILENAME nvarchar(255), @FILECHANGED bit ) as set nocount on; declare @CURRENTDATE datetime; declare @ERRORMESSAGE nvarchar(max) declare @FOUND nvarchar(max) declare @SAMPLETITLE nvarchar(50)
/* -- test that this stored procedure is actually executed when the form's saved: -- put in a raiserror (look in other code for examples) & see if it's executed when saving -- if it works, then: -- 1. validate that the selected ATTACHMENTTYPECODEID DESCRIPTION field actually contains the text "photo" -- 2. validate that the Title value matches a DESCRIPTION Column in the USR_CHILDPHOTOTITLECODE table --- if either fails, raise an error, informing user of problem & don't save. */
begin try if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
set @CURRENTDATE = getdate();
--Begin validating the Title and Type set @SAMPLETITLE = (SELECT TOP 1 DESCRIPTION FROM USR_CHILDPHOTOTITLECODE)
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
set @CURRENTDATE = getdate();
IF @TITLE = '' BEGIN SET @ERRORMESSAGE = 'TITLE is required - Please enter a valid TITLE' RAISERROR 100000 @ERRORMESSAGE END ELSE BEGIN set @FOUND = (SELECT DESCRIPTION FROM USR_CHILDPHOTOTITLECODE WHERE DESCRIPTION = @TITLE) IF @FOUND IS NULL BEGIN SET @ERRORMESSAGE = 'The "Title" you entered is NOT FOUND. Please enter a valid "Title" (ex: "' + @SAMPLETITLE + '")' RAISERROR 100000 @ERRORMESSAGE END END
SET @FOUND = (SELECT DESCRIPTION FROM DBO.SPROPPATTACHMENTTYPECODE WHERE ID = @ATTACHMENTTYPECODEID AND DESCRIPTION LIKE '%photo%') IF @FOUND IS NULL BEGIN SET @ERRORMESSAGE = 'Please select a type that is related to photo' RAISERROR 100000 @ERRORMESSAGE END -- End validating the Title and Type
if @FILECHANGED = 1 update dbo.[SPONSORSHIPOPPORTUNITYATTACHMENT] set [DATEENTERED] = @DATEENTERED, [TITLE] = @TITLE, [AUTHORID] = @AUTHORID, [SPROPPATTACHMENTTYPECODEID] = @ATTACHMENTTYPECODEID, [FILE] = @FILE, [FILENAME] = @DISPLAYEDFILENAME, [CHANGEDBYID] = @CHANGEAGENTID, [DATECHANGED] = @CURRENTDATE where [ID] = @ID; else update dbo.[SPONSORSHIPOPPORTUNITYATTACHMENT] set [DATEENTERED] = @DATEENTERED, [TITLE] = @TITLE, [AUTHORID] = @AUTHORID, [SPROPPATTACHMENTTYPECODEID] = @ATTACHMENTTYPECODEID, [CHANGEDBYID] = @CHANGEAGENTID, [DATECHANGED] = @CURRENTDATE where [ID] = @ID; end try
begin catch exec dbo.USP_RAISE_ERROR; return 1; end catch
return 0;
