create procedure dbo.USR_USP_TEMPSETCHILDCCHVALIDATEDFALSE_UPDATE
(
	@ID uniqueidentifier,
	@CHANGEAGENTID uniqueidentifier
)
as begin
	update dbo.USR_CHILDEXTENSION set CCHVALIDATED = 0 where SPONSORSHIPOPPORTUNITYCHILDID = @ID;
	
	return 0;
	
end
