create procedure dbo.USR_USP_TEMPSETCHILDFUNDEDNO_UPDATE
(
	@ID uniqueidentifier,
	@CHANGEAGENTID uniqueidentifier
)
as begin
	update dbo.USR_CHILDEXTENSION set FUNDED = 0 where SPONSORSHIPOPPORTUNITYCHILDID = @ID;
	
	return 0;
	
end
