create procedure dbo.USR_USP_TEMPSETCHILDREGISTRATIONDATENULL_UPDATE
(
	@ID uniqueidentifier,
	@CHANGEAGENTID uniqueidentifier
)
as begin
	update dbo.USR_CHILDEXTENSION set REGISTRATIONDATE = null where SPONSORSHIPOPPORTUNITYCHILDID = @ID;
	
	return 0;
	
end
