LoadImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USP_DATAFORMTEMPLATE_EDITLOAD_CHILDSIBLINGS(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@SIBLINGNAME nvarchar(50) = null output,
	@SIBLINGGENDERCODE tinyint = null output,
	@SIBLINGBIRTHYEAR smallint = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@TSLONG = TSLONG,
		@SIBLINGNAME = SIBLINGNAME,
		@SIBLINGGENDERCODE = SIBLINGGENDERCODE,
		@SIBLINGBIRTHYEAR = SIBLINGBIRTHYEAR
	from dbo.USR_CHILDSIBLINGS
  --where CHILDEXTENSIONID = @ID
	where ID = @ID

	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USP_DATAFORMTEMPLATE_EDIT_CHILDSIBLINGS ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @SIBLINGNAME nvarchar(50), @SIBLINGGENDERCODE tinyint, @SIBLINGBIRTHYEAR smallint ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- handle updating the data update dbo.USR_CHILDSIBLINGS set SIBLINGNAME = @SIBLINGNAME, SIBLINGGENDERCODE = @SIBLINGGENDERCODE, SIBLINGBIRTHYEAR = @SIBLINGBIRTHYEAR, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
