SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_FILENEXUSPHOTOYEARIMPORTBATCH_ADDROW ( @ID uniqueidentifier = null output, @BATCHID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @SEQUENCE int, @CHILDLOOKUPID nvarchar(10) = '', --@SPONSORSHIPOPPORTUNITYID uniqueidentifier = '', @PROFILEYEAR UDT_YEAR = null --@BATCHDATE date = null ) as
set nocount on;
declare @SPONSORSHIPOPPORTUNITYID uniqueidentifier;
if @ID is null set @ID = newid()
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
--if @BATCHDATE is null -- set @BATCHDATE = getdate()
-- Get the batch number of this batch & update the row: declare @BATCHNUMBER_ nvarchar(100) select @BATCHNUMBER_ = BATCHNUMBER from BATCH where ID = @BATCHID;
-- get the value from ChildLookupID value: select @SPONSORSHIPOPPORTUNITYID = ID from dbo.SPONSORSHIPOPPORTUNITY where LOOKUPID = @CHILDLOOKUPID;
begin try -- handle inserting the data INSERT INTO dbo.USR_FILENEXUSPHOTOYEAR_IMPORTBATCH( ID ,BATCHID ,[SEQUENCE] ,CHILDLOOKUPID ,SPONSORSHIPOPPORTUNITYID ,BATCHNUMBER ,PROFILEYEAR ,BATCHDATE ,ADDEDBYID ,CHANGEDBYID ,DATEADDED ,DATECHANGED ) VALUES ( @ID -- ID - uniqueidentifier ,@BATCHID -- BATCHID - uniqueidentifier ,@SEQUENCE -- SEQUENCE - int ,@CHILDLOOKUPID -- CHILDLOOKUPID - nvarchar(10) ,@SPONSORSHIPOPPORTUNITYID -- SPONSORSHIPOPPORTUNITYID - uniqueidentifier ,@BATCHNUMBER_ -- BATCHNUMBER - nvarchar(100) ,@PROFILEYEAR -- PROFILEYEAR - dbo.UDT_YEAR ,@CURRENTDATE -- BATCHDATE - date ,@CHANGEAGENTID -- ADDEDBYID - uniqueidentifier ,@CHANGEAGENTID -- CHANGEDBYID - uniqueidentifier ,@CURRENTDATE -- DATEADDED - datetime ,@CURRENTDATE -- DATECHANGED - datetime )
end try
begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0
