LoadImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_FILENEXUS_PHOTOIMPORTBATCH_EDITROW(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@SEQUENCE int = null output,
	@CHILDLOOKUPID nvarchar(10) = null output, 
	--@SPONSORSHIPOPPORTUNITYID uniqueidentifier = null output, 
	@PROFILEYEAR dbo.UDT_YEAR= null output 
	--@BATCHDATE date = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@SEQUENCE = SEQUENCE,
		@CHILDLOOKUPID = CHILDLOOKUPID, 
		--@SPONSORSHIPOPPORTUNITYID = SPONSORSHIPOPPORTUNITYID, 
		@PROFILEYEAR = PROFILEYEAR 
		--@BATCHDATE = BATCHDATE
	from dbo.USR_FILENEXUSPHOTOYEAR_IMPORTBATCH
	where ID = @ID
	
	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_FILENEXUS_PHOTOIMPORTBATCH_EDITROW( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @SEQUENCE int, @CHILDLOOKUPID nvarchar(10), --@SPONSORSHIPOPPORTUNITYID uniqueidentifier, @PROFILEYEAR dbo.UDT_YEAR --@BATCHDATE date ) as
set nocount on; declare @SPONSORSHIPOPPORTUNITYID uniqueidentifier;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
-- get the @SPONSORSHIPOPPORTUNITYID value based on ChildLookupID value in case user changed it: select @SPONSORSHIPOPPORTUNITYID = ID from dbo.SPONSORSHIPOPPORTUNITY where LOOKUPID = @CHILDLOOKUPID;
begin try -- handle updating the data UPDATE dbo.USR_FILENEXUSPHOTOYEAR_IMPORTBATCH SET [SEQUENCE] = @SEQUENCE ,CHILDLOOKUPID = @CHILDLOOKUPID ,SPONSORSHIPOPPORTUNITYID = @SPONSORSHIPOPPORTUNITYID ,PROFILEYEAR = @PROFILEYEAR --,BATCHDATE = @BATCHDATE ,CHANGEDBYID = @CHANGEAGENTID ,DATECHANGED = @CURRENTDATE where ID = @ID end try
begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
