create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_CHILDPHOTOADD_BATCHROW
(
    @ID uniqueidentifier = null output,
    @BATCHID uniqueidentifier,
    @CHANGEAGENTID uniqueidentifier = null,
    @SEQUENCE int,
    @SPONSORSHIPOPPORTUNITYLOOKUPID nvarchar(100) = null,
    @ATTACHMENTTYPECODEID uniqueidentifier = null,
    @PICTURETITLE nvarchar(100) = null,
    @FILENAME nvarchar(255) = null,
    @FILELOCATION nvarchar(1000) = null
)
as

set nocount on;

if @ID is null
    set @ID = newid()

if @CHANGEAGENTID is null  
    exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output

declare @CURRENTDATE datetime
set @CURRENTDATE = getdate()

begin try
	-- handle inserting the data
	insert into dbo.USR_BATCHCHILDPHOTOADD
		(ID, BATCHID, SEQUENCE, SPONSORSHIPOPPORTUNITYLOOKUPID, ATTACHMENTTYPECODEID, PICTURETITLE, FILENAME, FILELOCATION, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED)
    values
		(@ID, @BATCHID, @SEQUENCE, @SPONSORSHIPOPPORTUNITYLOOKUPID, @ATTACHMENTTYPECODEID, @PICTURETITLE, @FILENAME, @FILELOCATION, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE)
end try

begin catch
    exec dbo.USP_RAISE_ERROR
    return 1
end catch

return 0
