SPOperationImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USP_USR_BATCHTESTINGONLY_DELETE
(
	@ID uniqueidentifier,
	@CHANGEAGENTID uniqueidentifier
)
as begin
	--check deletion rules, if any

	-- use the system generated delete routine to allow proper recording of the deleting agent
	exec USP_BATCHTESTINGONLY_DELETEBYID_WITHCHANGEAGENTID @ID, @CHANGEAGENTID
	return 0;
	
end
