LoadImplementation.common:CreateProcedureSQL:

create procedure dbo.USP_DATAFORMTEMPLATE_EDITLOAD_BATCHTESTINGONLY(
		@ID uniqueidentifier,
		@DATALOADED bit = 0 output,
		@TSLONG bigint = 0 output,
	@SEQUENCE int = null output,
	@TESTNAME nvarchar(200) = null output,
	@TESTFLAG bit = null output,
	@TESTDESCRIPTION nvarchar(100) = null output,
	@SPONSORSHIPLOCATIONID uniqueidentifier = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@TSLONG = TSLONG,
		@SEQUENCE = SEQUENCE,
	@TESTNAME = TESTNAME,
	@TESTFLAG = TESTFLAG,
	@TESTDESCRIPTION = TESTDESCRIPTION,
	@SPONSORSHIPLOCATIONID = SPONSORSHIPLOCATIONID
	from dbo.USR_BATCHTESTINGONLY
	where ID = @ID

	return 0;

SaveImplementation.common:CreateProcedureSQL:

create procedure dbo.USP_DATAFORMTEMPLATE_EDIT_BATCHTESTINGONLY (
	@ID uniqueidentifier,
	@CHANGEAGENTID uniqueidentifier = null,
	@SEQUENCE int,
	@TESTNAME nvarchar(200),
	@TESTFLAG bit,
	@TESTDESCRIPTION nvarchar(100) ,
	@SPONSORSHIPLOCATIONID uniqueidentifier
)
as

	set nocount on;

	if @CHANGEAGENTID is null  
		exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output

	declare @CURRENTDATE datetime
	set @CURRENTDATE = getdate()

	begin try
		-- handle updating the data
		update dbo.USR_BATCHTESTINGONLY set
			SEQUENCE = @SEQUENCE,
			TESTNAME = @TESTNAME,
			TESTFLAG = @TESTFLAG,
			TESTDESCRIPTION = @TESTDESCRIPTION,
			SPONSORSHIPLOCATIONID = @SPONSORSHIPLOCATIONID,
			CHANGEDBYID = @CHANGEAGENTID,
			DATECHANGED = @CURRENTDATE
		where ID = @ID
	end try
	begin catch
		exec dbo.USP_RAISE_ERROR
		return 1
	end catch

return 0;
