create procedure dbo.USP_DATAFORMTEMPLATE_VIEW_TESTCHILD
(
	@ID uniqueidentifier,
	@DATALOADED bit = 0 output,
	@FIELD1 nvarchar(1000) = null output,
	@AGE tinyint = null output
)
as
	set nocount on;
	
  declare @SCID uniqueidentifier
  
	-- be sure to set this, in case the select returns no rows
	set @DATALOADED = 1;
	
select @AGE = 18
select @FIELD1 = 'This is the area'
  
	return 0;
