SQLFunctionSpec.CreateFunctionSQL:
create function dbo.USR_UFN_CHILD_GETCURRENTSTATUSFROMSPONSORSHIPEXTENSION( @OPPORTUNITYID uniqueidentifier ) returns nvarchar(100) with execute as caller as begin -- do work here and return a value declare @currentStatus nvarchar(100);
select @currentStatus = ct.[DESCRIPTION] from dbo.usr_sponsorshipextension se join dbo.USR_CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODE ct on ct.ID = se.CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID where se.SPONSORSHIPOPPORTUNITYID = @OPPORTUNITYID and se.ISCURRENT = 1
return @currentStatus end
