SPDataList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_APP_GETSPONSORSCHILDREN(@CONTEXTID uniqueidentifier) as begin set nocount on;
SELECT sponsor.ID, sponsor.NAME, so.ID as CHILDID, so_child.FIRSTNAME, so_child.LASTNAME, so_child.FIRSTNAME + ' ' + so_child.LASTNAME AS FULLNAME, countryconst.KEYNAME as COUNTRYNAME, countryconst.ID as COUNTRYID, so_child.AGE, SUBSTRING(so_child.BIRTHDATE,5,2) + '/' + SUBSTRING(so_child.BIRTHDATE,7,2) + '/' + SUBSTRING(so_child.BIRTHDATE,1,4) as BIRTHDATE, CASE so_child.GENDER WHEN 'Female' THEN 'Girl' WHEN 'Male' THEN 'Boy' END AS GENDER, so_child.GENDERCODE, childprojectconst.KEYNAME as PROJECTNAME, childprojectconst.ID AS PROJECTID, so.LOOKUPID as CHILDNO, so_child.PICTURE, note.TEXTNOTE FROM dbo.SPONSORSHIPOPPORTUNITY so INNER JOIN dbo.SPONSORSHIP on SPONSORSHIP.SPONSORSHIPOPPORTUNITYID = so.ID and SPONSORSHIP.STATUSCODE = 1 -- active INNER JOIN dbo.CONSTITUENT sponsor on SPONSORSHIP.CONSTITUENTID = sponsor.ID INNER JOIN dbo.SPONSORSHIPLOCATION sloc on sloc.ID = so.SPONSORSHIPLOCATIONID INNER JOIN dbo.SPONSORSHIPOPPORTUNITYCHILD so_child on so_child.ID = so.ID INNER JOIN SPONSORSHIPOPPORTUNITYNOTE note ON note.SPONSORSHIPOPPORTUNITYID = so.ID INNER JOIN SPROPPNOTETYPECODE notetype ON note.SPROPPNOTETYPECODEID = notetype.ID INNER JOIN dbo.CONSTITUENT childconst on childconst.ID = so_child.CONSTITUENTID -- This is the constituent record that has the note of type "Child Bio" LEFT OUTER JOIN dbo.USR_CHILDPROJECTEXTENSION projectext on sloc.FIELDOFFICEID = projectext.ID LEFT OUTER JOIN dbo.CONSTITUENT childprojectconst on childprojectconst.ID = projectext.ID -- This is the constituent record that has note of type "Project Bio" LEFT OUTER JOIN dbo.USR_COUNTRYEXTENSION countryext on projectext.COUNTRYEXTENSIONID = countryext.ID LEFT OUTER JOIN dbo.CONSTITUENT countryconst on countryconst.ID = countryext.ID -- This is the constituent record that has the note of type "Country Bio"
WHERE sponsor.ID = @CONTEXTID and notetype.DESCRIPTION = 'Child Bio' end
