SPDataList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_WEBSITE_GETAVAILABLECHILDRENFORPARTNER( @PARTNERLOOKUPID nvarchar(25), -- Partner Constituent Lookup ID @GENDER nvarchar(25), @AGEMIN nvarchar(10), @AGEMAX nvarchar(10), @COUNTRY nvarchar(25)
) as begin
set @GENDER = nullif(@GENDER, ''); -- ** NEED TO CONVERT TO INTEGER set @AGEMIN = nullif(@AGEMIN, ''); set @AGEMAX = nullif(@AGEMAX, ''); set @COUNTRY = nullif(@COUNTRY, '');
if lower(@PARTNERLOOKUPID) = 'general' begin exec USR_USP_WEBSITE_GETAVAILABLECHILDRENFORPARTNER_GENERAL @GENDER, @AGEMIN, @AGEMAX, @COUNTRY end else begin if lower(@PARTNERLOOKUPID) = 'addyounger' begin exec USR_USP_WEBSITE_GETAVAILABLECHILDRENFORPARTNER_ADDACHILD_YOUNGER @GENDER, @AGEMIN, @AGEMAX, @COUNTRY end else begin if lower(@PARTNERLOOKUPID) = 'addolder' begin exec USR_USP_WEBSITE_GETAVAILABLECHILDRENFORPARTNER_ADDACHILD_OLDER @GENDER, @AGEMIN, @AGEMAX, @COUNTRY end else begin if lower(@PARTNERLOOKUPID) = 'i5church' begin exec USR_USP_WEBSITE_GETAVAILABLECHILDRENFORPARTNER_I5CHURCH @GENDER, @AGEMIN, @AGEMAX, @COUNTRY end else begin if lower(@PARTNERLOOKUPID) = 'hillsalivegeneral' begin exec USR_USP_WEBSITE_GETAVAILABLECHILDRENFORPARTNER_HILLSALIVE_GENERAL @GENDER, @AGEMIN, @AGEMAX, @COUNTRY end end end end end end
